<?phpnamespace Config;// Create a new instance of our RouteCollection class.$routes = Services::routes();// Load the system's routing file first, so that the app and ENVIRONMENT// can override as needed.if (file_exists(SYSTEMPATH . 'Config/Routes.php')) {    require SYSTEMPATH . 'Config/Routes.php';}/* * -------------------------------------------------------------------- * Router Setup * -------------------------------------------------------------------- */$routes->setDefaultNamespace('App\Controllers');$routes->setDefaultController('Home');$routes->setDefaultMethod('index');$routes->setTranslateURIDashes(false);$routes->set404Override();$routes->setAutoRoute(true);/* * -------------------------------------------------------------------- * Route Definitions * -------------------------------------------------------------------- */// We get a performance increase by specifying the default// route since we don't have to scan directories.// home$routes->get('/', 'Home::index');$routes->get('/kamar', 'Home::kamar');$routes->get('/masuk', 'Home::masuk');$routes->get('/daftar', 'Home::daftar');$routes->get('/keluar', 'Home::keluar');// proses daftar dan masuk$routes->post('/daftar', 'Home::prosesDaftar');$routes->post('/masuk', 'Home::prosesMasuk');// admin$routes->get('/administrator', 'Admin::index');$routes->get('/administrator/data-admin', 'Admin::admin');$routes->get('/administrator/data-resepsionis', 'Admin::resepsionis');$routes->get('/administrator/kamar', 'Admin::kamar');$routes->get('/administrator/fasilitas-umum', 'Admin::fasilitasUmum');$routes->get('/administrator/profile', 'Admin::profile');$routes->get('/administrator/edit-fasilitas-umum/(:num)', 'Admin::editDataFasilitasUmum/$1');$routes->post('/administrator/edit-fasilitas-umum', 'Admin::prosesUpdateDataFasilitasUmum');$routes->get('/administrator/hapusFasilitasUmum/(:num)', 'Admin::deleteFasilitas/$1');$routes->get('/administrator/delete-data-kamar/(:num)', 'Admin::deleteKamar/$1');$routes->post('/administrator/tambah-fasilitas', 'Admin::tambahFasilitas');$routes->get('/administrator/edit-data-kamar/(:num)', 'Admin::editDataKamar/$1');$routes->post('/administrator/edit-data-kamar', 'Admin::updateDataKamar');$routes->post('/administrator/tambah-data-kamar', 'Admin::tambahDataKamar');$routes->get('/administrator/detail-pengguna/(:segment)', 'Admin::detail/$1');$routes->get('/administrator/delete-admin/(:segment)', 'Admin::deleteAdmin/$1');$routes->get('/administrator/delete-resepsionis/(:segment)', 'Admin::deleteResepsionis/$1');// resepsionis$routes->get('/page-resepsionis', 'Resepsionis::index');$routes->get('/page-resepsionis/detail/(:segment)', 'Resepsionis::detail/$1');// tamu$routes->get('/page-tamu', 'Tamu::index');$routes->get('/page-tamu/profile/bookingan', 'Tamu::bookingan');$routes->get('/page-tamu/profile', 'Tamu::profile');$routes->post('/page-tamu/profile', 'Tamu::perbaruiProfile');$routes->post('/page-tamu/pembayaran', 'Tamu::pembayaran');$routes->get('/page-tamu/kamar', 'Tamu::kamar');$routes->get('/page-tamu/booking-now/(:num)', 'Tamu::booking/$1');$routes->post('/page-tamu/booking-now', 'Tamu::orderNow');$routes->get('/page-tamu/profile/ubah-sandi', 'Tamu::ubahSandi');$routes->post('/page-tamu/profile/ubah-sandi', 'Tamu::updateSandi');/* * -------------------------------------------------------------------- * Additional Routing * -------------------------------------------------------------------- * * There will often be times that you need additional routing and you * need it to be able to override any defaults in this file. Environment * based routes is one such time. require() additional route files here * to make that happen. * * You will have access to the $routes object within that file without * needing to reload it. */if (file_exists(APPPATH . 'Config/' . ENVIRONMENT . '/Routes.php')) {    require APPPATH . 'Config/' . ENVIRONMENT . '/Routes.php';}